#pragma once

#include <bits/stdc++.h>

using namespace std;

#define _ ios_base::sync_with_stdio(0); cin.tie(0);
#define all(a) (a).begin(), (a).end()
#define endl '\n'
#define ff first
#define ss second
#define pb push_back

typedef long long ll;

const int INF = 0x3f3f3f3f;
const ll LINF = 0x3f3f3f3f3f3f3f3fll;

// NÃºmero de pedidos, itens, corredores.
ll o, i, a, lb, ub;

vector<vector<pair<int, int>>> orders, aisles;

class Solution {
    public:
        vector<int> mOrders, mAisles;
        
        double calcObj() {
            if(mAisles.size() == 0) return 0.0;
            return (static_cast<double>(mOrders.size()) / mAisles.size());
        }

        void print(){
            cout << mAisles.size() << endl;
            for(size_t j = 0; j < mAisles.size(); j++){
                cout << mAisles[j] << endl;
            }

            cout << mOrders.size() << endl;
            for(size_t j = 0; j < mOrders.size(); j++){
                cout << mOrders[j] << endl;
            }
        }
};